	          edgeMarks = 0;
	      for (var key in this.graphdata.dots) {
	        dotsLen++;
	        if (this.graphdata.dots[key].isActive) {
	          dotActives++;
	        }
	        if (this.graphdata.dots[key].isActiveChTurningDot) {
	          dotChTurnings++;
	        }
	        if (this.graphdata.dots[key].isMark) {
	          dotMarks++;
	        }
	      }
