	          edgeActives++;
	        }
	        if (this.graphdata.edges[_key6].isMark) {
	          edgeMarks++;
	        }
	      }
	      if (dotsLen === 0) {
	        state.dot = 0;
	        state.chTurningDot = 0;
	      } else {
	        state.dot = dotActives === 0 ? 1 : 2;
	        state.chTurningDot = dotChTurnings === 0 ? 1 : 2;
	      }
	      if (edgesLen === 0) {
	        state.edge = 0;
	      } else {
	        state.edge = edgeActives === 0 ? 1 : 2;
