			this.COMPLETE_STATE = 3; //动画结束状态
			this.SHOW_LISTS_LENGTH = 2; // 显示数据记录的长度
			this.MAX_LISTS_LENGTH = 6; // 最多记录6条数据
			this.MAX_BALL_WIDTH = 5.75 * this.fontSize; // 最大小球的宽度
