                this.reset();
            }
            AnimateControl._inst = null;
        }

        /**
         * 初始化事件
         */

    }, {
        key: 'init',
        value: function init(app) {
            this.app = app;
            // Todo 
            this.$ball = this.app.$view.find('.ballWrap'); // 球
            this.$ballContain = this.app.$view.find('.scrollBall');
            this.$block = this.app.$view.find('.squareBar'); // 木块
            this.$slope = this.app.$view.find('.triangleCover'); // 斜坡
            this.$flat = this.app.$view.find('.slopeBoardCover'); // 平地
            this.$ident = this.app.$view.find('.identifying'); // 定义高度

