            this._blockLeft = Number.parseFloat(this.$block.css('left'));
            this._blockWidth = this.$block.width();
            this._ballWidth = this.$ball.width();
            this._slopeHeight = this.$slope.height();
            this._slopeWidth = this.$slope.width();
            this._rate = this._slopeHeight / this._slopeWidth; //  斜坡宽高比
            this._slopeLeft = Number.parseFloat(this.$slope.css('left'));
            this._flatWidth = this.$flat.width();
            this._maxDis = this._flatWidth - this._blockWidth; // 最大动能时移动的距离
            this._maxKinetic = this.app.config.MAX_HEIGHT * this.app.config.MAX_WEIGHT; // 最大动能
            this._ratio = 1;
            return this;
        }

        /**
         * 创建滑动动画
         */

    }, {
        key: '_createAnimate',
        value: function _createAnimate() {
            var ballLeft = Number.parseFloat(this.$ball.css('left')),
                ballTop = Number.parseFloat(this.$ball.css('top')),
                curKinetic = 0,
                slopeX = 0,
                slopeY = 0,
                flatX = 0,
                interval = 0,
                intervalTime = 0,
                flatTime = 0,
                slopeTime = 0,
                self = this;
            this._ballWidth = this.$ballContain.width();
            slopeX = this._slopeLeft + this._slopeWidth - ballLeft; // 斜坡向右移动距离
            slopeY = slopeX * this._rate; // 斜坡向下移动距离
            curKinetic = this.app.data.ballHeight * this.app.data.ballWeight; // 动能
            interval = this._blockLeft - this._slopeLeft - this._slopeWidth - this._ballWidth / 2 + this._maxRadius / 2; // 木块距离斜坡距离，加入this._maxRadius / 2是让小球靠近木块左面的中间
            flatX = curKinetic / this._maxKinetic * this._maxDis; // 水平应移动距离
            slopeTime = this.app.data.ballHeight / this.app.config.MAX_HEIGHT + this.app.config.SLOPE_MIN_TIME; // 斜坡滚动时间
            intervalTime = this.app.config.INTERVAL_MAX_TIME - curKinetic / this._maxKinetic / 5; // 平地上小球撞木块前移动的时间
            flatTime = this.app.config.FLAT_MIN_TIME + curKinetic / this._maxKinetic * 2; // 平地上小球与木块一起移动时间
            this.tl = new TimelineMax({
                onComplete: this._complete.bind(this) // 动画结束后执行的方法
            });
            // tweenMax动画
            this.tl.to(this.$ball, slopeTime, { // 小球斜坡下滚动画
                x: slopeX * self._ratio,
                y: slopeY * self._ratio,
                z: 1,
                ease: Power1.easeIn
            }).to(this.$ballContain, slopeTime, { // 小球斜坡自滚动画
                rotation: this._rotationCount(slopeX * self._ratio, this.app.config.SLOPE_COUNT) * 360,
                z: 1,
                ease: Power1.easeIn
            }, '-=' + slopeTime).to(this.$ball, intervalTime, { // 小球撞木块前滚动动画
                x: '+=' + interval,
                z: 1,
                ease: Linear.easeNone
            }).to(this.$ballContain, intervalTime, { // 小球撞木块前自滚动画
                rotation: '+=' + this._rotationCount(interval, this.app.config.FLAT_COUNT) * 360,
                z: 1,
                ease: Linear.easeNone
            }, '-=' + intervalTime).to([this.$ball, this.$block], flatTime, { // 小球平地移动动画
                x: '+=' + flatX,
                z: 1,
                ease: Power1.easeOut
            }).to(this.$ballContain, flatTime, { // 小球平地自滚动画
                rotation: '+=' + this._rotationCount(flatX, this.app.config.FLAT_COUNT) * 360,
                z: 1,
                ease: Power1.easeOut
            }, '-=' + flatTime);
        }
        /**
        * 创建滑动动画
        */

    }, {
        key: '_reCreateAnimate',
        value: function _reCreateAnimate() {
            var ballLeft = Number.parseFloat(this.$ball.css('left').replace('px', '') * this._ratio + 'px'),
                ballTop = Number.parseFloat(this.$ball.css('top').replace('px', '') * this._ratio + 'px'),
                curKinetic = 0,
                slopeX = 0,
                slopeY = 0,
                flatX = 0,
                interval = 0,
                intervalTime = 0,
                flatTime = 0,
                slopeTime = 0,
                self = this;
            this._ballWidth = this.$ballContain.width();
            slopeX = this._slopeLeft + this._slopeWidth - ballLeft; // 斜坡向右移动距离
            slopeY = slopeX * this._rate; // 斜坡向下移动距离
            curKinetic = this.app.data.ballHeight * this.app.data.ballWeight; // 动能
            slopeTime = this.app.data.ballHeight / this.app.config.MAX_HEIGHT + this.app.config.SLOPE_MIN_TIME; // 斜坡滚动时间
            interval = this._blockLeft - this._slopeLeft - this._slopeWidth - this._ballWidth / 2 + this._maxRadius / 2; // 木块距离斜坡距离，加入this._maxRadius / 2是让小球靠近木块左面的中间
            flatX = curKinetic / this._maxKinetic * this._maxDis; // 水平应移动距离
            this.tl = new TimelineMax({
                onComplete: this._complete.bind(this) // 动画结束后执行的方法
            });
            // tweenMax动画
            this.tl.to(this.$ball, 0.0001, { // 小球斜坡下滚动画
                x: slopeX,
                y: slopeY,
                z: 1
            }).to(this.$ballContain, 0, { // 小球斜坡自滚动画
                rotation: this._rotationCount(slopeX, this.app.config.SLOPE_COUNT) * 360,
                z: 1
            }).to(this.$ball, 0, { // 小球撞木块前滚动动画
                x: '+=' + interval,
                z: 1
            }).to(this.$ballContain, 0, { // 小球撞木块前自滚动画
                rotation: '+=' + this._rotationCount(interval, this.app.config.FLAT_COUNT) * 360,
                z: 1
            }).to([this.$ball, this.$block], flatTime, { // 小球平地移动动画
                x: '+=' + flatX,
                z: 1,
                ease: Power1.easeOut
            }).to(this.$ballContain, 0, { // 小球平地自滚动画
                rotation: '+=' + this._rotationCount(flatX, this.app.config.FLAT_COUNT) * 360,
                z: 1
            });
            console.log('动画后', Number.parseFloat(this.$block.css('left')));
        }
    }, {
        key: 'resizeScreen',
        value: function resizeScreen(data) {
            //console.log('this.$slope.css',this.$slope.css('left'))
            this._maxRadius = this.app.config.MAX_BALL_WIDTH * data / 2;
            this._blockLeft = Number.parseFloat(this.$block.css('left'));
            this._blockWidth = this.$block.width();
            this._ballWidth = this.$ball.width();
