        }

        /**
         * 滚动圈数
         */

    }, {
        key: '_rotationCount',
        value: function _rotationCount(dis, num) {
            var count = 0;
            count = dis / (2 * Math.PI * this._ballWidth / 2) * num;
            return count;
        }

        /**
         * 操作动画
         */

    }, {
        key: 'operate',
        value: function operate(state, preState) {
            if (state === this.app.config.COMPLETE_STATE) {
                return;
            }
            switch (preState) {
                case this.app.config.INIT_STATE:
                    this.forbideFull(true);
                    this._createAnimate();
                    this._play();
                    this._hideIdent();
                    break;
                case this.app.config.RUN_STATE:
                    this._pause();
                    break;
                case this.app.config.PAUSE_STATE:
                    this._resume();
                    break;
                case this.app.config.COMPLETE_STATE:
                    this.reset();
                    this._showIdent();
                    break;
                default:
                    this._createAnimate();
                    this._play();
            }
        }

        /**
         * 状态重置
         */

    }, {
        key: 'reset',
        value: function reset() {
            this.forbideFull(false);
            this._progress();
            this._pause();
            this.tl.kill();
            this.tl = null;
        }

        /**
         * 播放动画
         */

    }, {
        key: '_play',
        value: function _play() {
            this.tl.play();
        }

        /**
         * 暂停动画
         */

    }, {
        key: '_pause',
        value: function _pause() {
            this.tl.pause();
        }

        /**
         * 继续动画
         */

    }, {
        key: '_resume',
        value: function _resume() {
            this.tl.resume();
        }

        /**
         * 控制进度
         * @param rate:Number  控制进度0-1之间，小于0为0，大于1为1 
         */

    }, {
        key: '_progress',
        value: function _progress(rate) {
            typeof rate === 'number' ? this.tl.progress(rate) : this.tl.progress(0);
        }

        /**
         * 动画结束后执行
         */

    }, {
        key: '_complete',
        value: function _complete() {
            //console.log('动画结束啦啦啦')
            this.app.data.curState = this.app.config.COMPLETE_STATE;
            this.forbideFull(false);
        }

        /**
         * 显示高度定义框
         */

    }, {
        key: '_showIdent',
        value: function _showIdent() {
            if (this.$ident.css('display') === 'none') {
                this.$ident.show();
            }
        }

        /**
         * 隐藏高度定义框
         */

    }, {
        key: '_hideIdent',
        value: function _hideIdent() {
