         * 1初始状态按钮全部可点 2运动和暂停状态所有按钮都不可以点  3结束后高度，质量，状态重置，一键重置可点
         * 最小化前后，按钮的状态都是一致的，而最小化时，按钮都隐藏了，因此最小化时无需处理
         */

    }, {
        key: 'watchState',
        value: function watchState(state) {
            if (state === this.app.config.INIT_STATE) {
                this.plugin.lock = false;
                this.plugin2.lock = false;
                this.$start.removeClass("click_disabled");
                this.$reset.addClass("click_disabled");
                this.$resetAll.removeClass("disabled");
                this.$slide.removeClass('disabled');
            } else if (state === this.app.config.RUN_STATE) {
                this.plugin.lock = true;
                this.plugin2.lock = true;
                this.$start.addClass("click_disabled");
                this.$resetAll.addClass("disabled");
                this.$otherLight.addClass("disabled");
                this.$slide.addClass('disabled');
                //todo 高度和质量置灰
            } else if (state === this.app.config.COMPLETE_STATE) {
                this.plugin.lock = false;
                this.$reset.removeClass("click_disabled");
                this.$resetAll.removeClass("disabled");
                this.$otherLight.removeClass("disabled");
                this.$slide.removeClass('disabled');
            }
        }

        /**
         * 渲染滑块的位置
         */

    }, {
        key: 'renderHandle',
        value: function renderHandle(height) {
            this.plugin.curVal = height * 10;
            height = height * 10 % 10 !== 0 ? height.toFixed(1) : height.toFixed(0);
            this.$text.html(height);
            this.plugin2.curVal = height * 10;
        }

        /**
         * 改变质量
         */

    }, {
        key: 'watchWeight',
        value: function watchWeight(weight) {
            //选中相应质量的tab
            //更改显示质量的值
            //关闭质量下拉框
            var index = weight / 5 - 1; //对应质量tab的下标
            var $target = this.$list.find("a").eq(index);
            var value = $target.find("span").html();
            if (!$target.hasClass("active")) {
                $target.addClass("active");
            }
            $target.siblings().removeClass("active");
            if (this.$arrow.hasClass("active")) {
                this.hideArrow();
            }
            this.$num_ipt.text(value);
            this.watchSize(weight);
        }

        /**
         * 改变小球大小
         */

    }, {
        key: 'watchSize',
        value: function watchSize(weight) {
            this.$ball.attr("class", 'ballDiv ballSize' + weight);
        }

        /**
         * 析构
         */

    }, {
        key: 'destroy',
        value: function destroy() {
            this.$otherLight.off();
            this.$list.off("click", "a", this.clickWeight);
            this.$start.off();
            this.$reset.off();
            this.$resetAll.off();
            FunctionControl._inst = null;
        }
    }]);

    return FunctionControl;
