		}, {
			key: 'bindEvent',
			value: function bindEvent() {
				var that = this;
				this.container.on('click', 'a', function (e) {
					var $target = $(this);
					var type = $target.data('type');
					if ($target.hasClass('click_disabled') || that.app.data.animateClassName && !$target.hasClass('reset')) {
						return;
					}

					if ($target.hasClass('reset')) {
						that.app.data.polarizerP = 0;
						that.app.data.polarizerQ = 0;
						that.app.data.polarizerPAngle = 0;
						that.app.data.polarizerQAngle = 0;
						that.app.data.animateClassName = '';
						return;
					}
					if ($target.hasClass('rotate')) {
						that.app.data['animateClassName'] = type;
					}
					that.app.data[type] = +!that.app.data[type];
				});

				function transitionEnd(e) {
					that.app.data.animateClassName = '';
				}

				this.app.$view.find('.polaroid').each(function (i, el) {
					el.addEventListener('transitionend', transitionEnd, false);
				});
			}

