					$el.find('.btns-primary-txt').html(this.app.config.btnTexts[value]);
					if (value) {
						$el.closest('ul').find('.rotate').removeClass('click_disabled');
					} else {
						$el.closest('ul').find('.rotate').addClass('click_disabled');
					}
				}

				if (reset) {
					this.container.find('.reset').addClass('click_disabled');
				} else {
					this.container.find('.reset').removeClass('click_disabled');
				}
			}
		}, {
