	          var pos = that.getPagePos(e, scale);
	          var cssPercent = (pos.x - slideOffset.left / scale) / totalWidth * 100;
	          if (cssPercent > 100) {
	            cssPercent = 100;
	          }
	          if (cssPercent < 0) {
	            cssPercent = 0;
