	var PtextPos = function PtextPos(dom) {
	  var LEFT = 0;
	  var TOP = 0;
	  var IS_CHANGE_1 = dom.moveLineOneW < 1.5 * _basic.Viewdom.alertWid;
	  var IS_CHANGE_2 = dom.moveLineTwoW < 1.5 * _basic.Viewdom.alertWid;
	  switch (true) {
	    /* 预设一P自适应*/
	    case dom.line1R > -90 && dom.line1R < 0 && dom.$parent.name === 'pre1' && IS_CHANGE_1:
	      {
	        LEFT = 0.2;
	        TOP = -1.5;
	        break;
	      }
	    case dom.line2R > -90 && dom.line2R < 0 && dom.$parent.name === 'pre1':
	      {
	        LEFT = -0.1;
	        TOP = -1.7;
	        break;
	      }
	    case dom.line2R > 0 && dom.line2R < 180 && dom.line1R < 40 && dom.$parent.name === 'pre1':
	      {
	        LEFT = 0.7;
	        TOP = 0.3;
	        break;
	      }
	    case dom.line2R > 90 && dom.line1R > 90 && dom.line1R < 180 && dom.$parent.name === 'pre1':
	      {
	        LEFT = 0.2;
	        TOP = -1.75;
	        break;
	      }
	    /* 预设二P自适应*/
	    case dom.line1R < 0 && dom.$parent.name === 'pre2' && IS_CHANGE_1:
	      {
	        LEFT = 1.5;
	        TOP = -0.1;
	        break;
	      }
	    case dom.line2R < 0 && dom.$parent.name === 'pre2' && IS_CHANGE_2:
	      {
	        LEFT = 1.5;
	        TOP = -0.1;
	        break;
	      }
	    case dom.line1R < 180 && dom.line1R > 90 && dom.$parent.name === 'pre2':
	      {
	        LEFT = -0.2;
	        TOP = -1.6;
	        break;
	      }
	    case dom.line1R < 90 && dom.line2R < 90 && dom.line2R > 0 && dom.$parent.name === 'pre2' && !IS_CHANGE_2:
	      {
	        LEFT = 1.7;
	        TOP = -1.6;
	        break;
	      }
	    case dom.line1R < 90 && dom.$parent.name === 'pre2' && dom.line2R > 90 && dom.line2R < 180 && IS_CHANGE_2:
	      {
	        LEFT = -0.3;
	        TOP = -1.3;
	        break;
	      }
	    case dom.line1R < 90 && dom.line2R < 90 && dom.line2R > 0 && dom.$parent.name === 'pre2' && IS_CHANGE_2:
	      {
	        LEFT = 1.9;
	        TOP = -1.7;
	        break;
	      }
	    case dom.line1R < 90 && dom.line1R > 0 && dom.line2R < 180 && dom.line2R > 90 && dom.$parent.name === 'pre2':
	      {
	        LEFT = 1.9;
	        TOP = -1.7;
	        if (dom.line2R < 145) {
	          LEFT = 0.3;
	        }
	        break;
	      }
	    /* 预设三P自适应*/
	    case dom.line1R > 180 && dom.$parent.name === 'pre3':
	      {
	        LEFT = 0.8;
	        TOP = -0.4;
	        break;
	      }
	    case dom.line1R < 0 && dom.line2R < 0 && dom.$parent.name === 'pre3':
