        else {
            var phaseDomList = layoutArticle.find('.rdtl_arit_para');
            var phaseLength = phaseDomList.length;
            if (phaseLength <= 0) {
                if (titleDom.length > 0) {
                    phaseIndex = 0;
                    sentenceIndex = 0;
                }
                else {
                    return;
                }
            }
            else {
                if (this.limitPhase === -1) {
                    for (var i = 0; i < phaseLength; i++) {
                        var phaseDom = phaseDomList.eq(i);
                        var phaseTop = phaseDom.position().top;
                        var phaseHeight = phaseDom.height();
                        var phaseAllTop = phaseTop + phaseHeight;
                        if (phaseAllTop >= scrollHeight) {
                            phaseIndex = i + 1;
                            break;
                        }
                    }
                    if (phaseIndex === -1) {
                        phaseIndex = phaseLength;
                    }
                }
                else {
                    phaseIndex = this.limitPhase;
                }
                if (this.limitSentence === -1) {
                    var phaseDom = phaseDomList.eq(phaseIndex - 1);
                    var sentenceList = phaseDom.find('.rdtl_arit_sentence');
                    var sentencelength = sentenceList.length;
                    if (sentencelength <= 0) {
                        return;
                    }
                    for (var i = 0; i < sentencelength; i++) {
                        var sentenceDom = sentenceList.eq(i);
                        var sentenceTxt = sentenceDom.find('.txt').eq(0);
                        var sentenceTop = sentenceTxt[0].offsetTop;
                        var sentenceHeight = sentenceTxt.height();
                        var sentenceAllTop = phaseDom.position().top + sentenceTop + sentenceHeight;
                        if (sentenceAllTop >= scrollHeight) {
                            sentenceIndex = i;
                            break;
                        }
                    }
                    if (sentenceIndex === -1) {
                        sentenceIndex = sentencelength - 1;
                    }
                }
                else {
                    sentenceIndex = this.limitSentence;
                }
            }
        }
        if (this.readingSentence[0] != phaseIndex || this.readingSentence[1] != sentenceIndex) {
            this.$store.commit('CHANGE_READING_SENTENCE', [phaseIndex, sentenceIndex]);
        }
    };
    return ArticleComponent;
}(Vue));
