        /**
         * 计算亮暗条纹排版数据
         * @param light: [Number] 波长λ的值
         * @param d: [Number] 双缝间距d的值
         */

    }, {
        key: '_circulateLightData',
        value: function _circulateLightData(light, d) {
            if (light === 0) {
                this._x = d; //暗条纹的高度,单位为mm;
                return 'white';
            } else {
                this._x = light / (3000 * d); //暗条纹的高度,单位为mm;
            }
            var dataArr = [1],
                //渲染亮暗条纹的数据
            tempArr = void 0; //亮暗条纹的中间数据

            this.numb = Math.floor(this._heightMm / (2 * this._x)) - 1; //色块总数的一半-1;
            var temp = this.numb % 3; //条纹中多余的高度（不够凑成一个△x的那块）
            for (var i = 0; i < Math.floor(this.numb / 3); i++) {
                dataArr.push(0, 1);
            }
            if (temp > 0) dataArr.push(0);
            //拼凑渲染的数据（亮暗条纹的顺序以及个数）；
            tempArr = dataArr.concat().reverse();
            dataArr.shift(1);
            dataArr = tempArr.concat(dataArr);
            return dataArr;
        }

        /**
         * 根据生成的数据渲染视图
         * @param dataArr: [array] 数据
         */

    }, {
        key: '_renderView',
        value: function _renderView(dataArr, curLight, needAnima) {
            //如果为收到数据，则渲染
            if (!dataArr) {
                return;
            }
            //如果当为白光，另做处理
            if (dataArr === 'white') {
                this._$lightBlock.html("");
                this._$waveLenCom.hide();
                if (!needAnima) {
                    this._$whiteBlock.show();
                    this._$twoslitinter.show();
                }
                return;
            }
            this._$lightBlock.hide();
            this._$twoslitinter.hide();
            if (!needAnima) {
                this._$waveLenCom.show();
            }
            this._$whiteBlock.hide();
            var len = (dataArr.length - 1) / 2,
                html = '',
                //渲染的html
            dimHeight = this._unitEm * this._x,
                //实际上暗条纹的高度,
            highHeight = this._unitEm * this._x * 2,
                //实际上亮条文的高度
            color = this._getColor(curLight),
                isShowFormula = this._x < 1.35 ? "none" : "block"; //是否显示公式

            for (var i = len; i >= -len; i--) {
                var temp = len - i;
                color = dataArr[temp] === 0 ? "black" : this._getColor(curLight);
                var isBlack = dataArr[temp] === 0 ? "blackrect" : color + 'Rect',
                    height = dataArr[temp] === 0 ? dimHeight : highHeight;
                //拼接所需的html
                html += '<div data-height=' + dimHeight + ' data-idx=' + i + ' style="height: ' + height + 'px;" class="' + isBlack + ' rectCom "><div class="whiteBorder"></div><div style="display: ' + isShowFormula + ';"  class="formula ' + color + '"><i class="fontCambriai">\u03B4</i><span class="yahei50">=|</span><i class="fontCambriai">r<sub class="yahei36">1</sub></i><span class="yahei50">-</span><i class="fontCambriai">r<sub class="yahei36">2</sub></i><span class="yahei50"> |=';
                var abs = Math.abs(i);
                if (abs === 0) {
                    html += '0</span>';
                } else if (abs === 2) {
                    html += '</span><i class="fontCambriai camleft">\u03BB</i>';
                } else if (abs === 1) {
                    html += '</span><i class="fontCambriai camleft">\u03BB</i><span class="yahei50">/2</span>';
                } else if (abs % 2 === 0) {
                    html += abs / 2 + '</span><i class="fontCambriai camleft">\u03BB</i>';
                } else {
                    html += abs + '</span><i class="fontCambriai camleft">\u03BB</i><span class="yahei50">/2</span>';
                }
                html += '</div></div>';
            }
            this._$lightBlock.html(html);
            if (!needAnima) this._$lightBlock.show();
        }
        /**************************************
         * 对外接口
         *************************************/
        /**
         * 当改变颜色或者双缝间距时，计算数据并渲染视图；
         */

    }, {
