        /**
         * 获取index所在的条纹中心距中心轴的高度，中心轴下方的条纹返回负值
         * @param index 当前条纹的index，中心轴往上为1,2,3...,往下为-1,-2，-3
         * @param unitHeight 单条暗条纹的高度
         * @private
         */

    }, {
        key: "_getAbsHeight",
        value: function _getAbsHeight(index, unitHeight) {
            if (index === "") index = 1;
            var height = 0;
            var absIndex = Math.abs(index);
            var div = Math.floor(absIndex / 2),
                remainder = absIndex % 2;
            if (remainder === 1) {
                height = (div * 3 + 0.5 * remainder + 1) * unitHeight;
            } else {
                height = div * 3 * unitHeight;
            }
            if (index < 0) height = -height;
            return height;
        }

        /**
         * 根据当前最大的条纹索引，设置上下∆x的位置
         * @param idx 图形上最上方的条纹的index
         * @param unitHeight 单条暗条纹的高度
         * @param curIndex 当前选中条纹的index，如果为空，则是没选中任何条纹
         * @private
