    }, {
        key: "_setXPosition",
        value: function _setXPosition(idx, unitHeight, curIndex) {
            if (idx == 0) return;
            this.$topx.show();
            this.$bottomx.show();
            var minHeight = this._emToPx(0.5);
            if (unitHeight <= minHeight) {
                this.$topx.addClass("miniWaveLen");
                this.$topx.removeClass("bigWaveLen");
                this.$bottomx.addClass("miniWaveLen");
                this.$bottomx.removeClass("bigWaveLen");
