                remainder = idx % 2,
                light_height = (div - 1) * 3 * unitHeight; //最远的亮条纹到中心的距离
            if (remainder === 0) {
                top_height = light_height - 1.5 * unitHeight;
                bottom_height = light_height;
            } else {
                top_height = light_height + 1.5 * unitHeight;
                bottom_height = light_height;
            }
            if (curIndex >= 0 && curIndex !== "") {
                if (curIndex % 2 !== 0) bottom_height = top_height;
                this.$topx.hide();
            } else if (curIndex < 0) {
                if (curIndex % 2 === 0) top_height = bottom_height;
                this.$bottomx.hide();
            }
            this.$topx.css("bottom", top_height * (2 / 3));
            this.$bottomx.css("top", bottom_height * (2 / 3));
        }

