            var sp1 = s1.y + (p.y - s1.y) * percentage - this._emToPx(4);
            var sp2 = s2.y + (p.y - s2.y) * percentage;
            var py = 0;
            if (isSpecial) {
                py = this._emToPx(7.57);
            } else {
                py = s2.y + (p.y - s2.y) * 0.95;
