            var d_x = this._emToPx(11.3333333333 + 0.7124432232378175); //d字的left值，加上自身宽度的一半
            var temp = (d_x - s1.x) / (p.x - s1.x);
            var top1 = s1.y + (p.y - s1.y) * temp;
            var top2 = s2.y + (p.y - s2.y) * temp;
            var dTop = (top1 + top2) / 2 - this._emToPx(1.233668);
            this.$d.css("top", dTop);
        }

