  return sceneArray;
}

function clearUp() {
  for (var i = 0, len = sceneArray.length; i < len; i++) {
    // let basesence = sceneArray[i];
    if (sceneArray[i] instanceof _BaseScene2.default) {
      sceneArray[i].close();
    }
    delete sceneArray[i];
  }
  sceneArray = [];
  isFirstPopShow = true;

