  $(_presenter2.default.view).off(TRIGGER_TYPE.ENTER_MODIFY);
  $(_presenter2.default.view).off(TRIGGER_TYPE.ENTER_CREATE);
}

function updateCamera(width, height) {
  for (var i = 0, len = sceneArray.length; i < len; i++) {
    if (sceneArray[i] instanceof _BaseScene2.default && sceneArray[i].camera) {
      sceneArray[i].camera.aspect = width / height;
      sceneArray[i].camera.updateProjectionMatrix();
    }
  }
}

function renderScence(renderer) {
  if (sceneArray.length > 0) {
    var eScene = lastScence();
    eScene.updateRender();
    if (eScene.scene && eScene.camera) {
      renderer.render(eScene.scene, eScene.camera);
    }
  }
}

