  var quatInverse = quat.clone().inverse();

  // events

  var changeEvent = { type: 'change' };
  var startEvent = { type: 'start' };
  var endEvent = { type: 'end' };

  // button events

  // var interval = null;
  // var timeout = null;
  var zoomStatus = 'normal';

  this.getZoomStatus = function () {
