function isControlButtonUnable() {
  return $(presenterView).find('.bottom-wangge-control_center').hasClass('btn_on_disabled') || $(presenterView).find('.shape_show_setting_center').hasClass('btn_on_disabled');
}

function isHideControlButton(isHide) {
  if (isHide) {
    $(presenterView).find('.bottom-control-view').hide();
  } else {
    $(presenterView).find('.bottom-control-view').show();
  }
}

/**
 * 展开，还原按钮状态
 */
function setButtonNormal(btn) {
  if (btn === 'left') {
    $(presenterView).find('.progress-top-button').removeClass('s_disaple');
  } else {
    $(presenterView).find('.progress-bottom-button').removeClass('s_disaple');
  }
}
function setButtonDisable(btn) {
  if (btn === 'left') {
    $(presenterView).find('.progress-top-button').addClass('s_disaple');
    $(presenterView).find('.progress-bottom-button').removeClass('s_disaple');
  } else {
    $(presenterView).find('.progress-bottom-button').addClass('s_disaple');
    $(presenterView).find('.progress-top-button').removeClass('s_disaple');
  }
}

