function hideZoomBtns(type) {
  if (type) {
    $(presenterView).find(type).hide();
  } else {
    $(presenterView).find(ZOOM_BTNS.ZOOM_IN).hide();
    $(presenterView).find(ZOOM_BTNS.ZOOM_OUT).hide();
  }
}

/**
 * 显示放大缩小按钮
 * type: 'in', 'out'
 */

function showZoomBtns(type) {
  if (type) {
    $(presenterView).find(type).show();
  } else {
    $(presenterView).find(ZOOM_BTNS.ZOOM_IN).show();
    $(presenterView).find(ZOOM_BTNS.ZOOM_OUT).show();
  }
}

/**
 * 更新放大缩小按钮显隐
 */

var oldZoomStatus = '';
function updateZoomBtns(zoomStatus) {
  zoomStatus = zoomStatus || oldZoomStatus;
  oldZoomStatus = zoomStatus;
  $(presenterView).find(ZOOM_BTNS.ZOOM_IN).removeClass(ZOOM_BTNS.ZOOM_DISABLE);
  $(presenterView).find(ZOOM_BTNS.ZOOM_OUT).removeClass(ZOOM_BTNS.ZOOM_DISABLE);
  if (zoomStatus === 'min') {
    $(presenterView).find(ZOOM_BTNS.ZOOM_IN).addClass(ZOOM_BTNS.ZOOM_DISABLE);
  } else if (zoomStatus === 'max') {
    $(presenterView).find(ZOOM_BTNS.ZOOM_OUT).addClass(ZOOM_BTNS.ZOOM_DISABLE);
  }
}

