      scope.current.show();

      scope.object.joint.remove(scope.current);
      scope.cross = scope.object.joint.cross(scope.current);

      scope.moved.copy(scope.current.position);
      var intersect = scope.doIntersectObjects(event, [scope.location]);
      if (intersect) {
        var position = intersect.point;
        scope.current.position.copy(position);
        scope.object.freshCount(scope.current);
        (0, _projectionAction.updateCreatePosition)(scope.current.index, scope.current.position);
      }
    }
  }, {
    key: 'doIntersectObjects',
    value: function doIntersectObjects(pointer, objects) {
      var rect = scope.domElement.getBoundingClientRect();
      var x = (pointer.clientX - rect.left) / rect.width;
      var y = (pointer.clientY - rect.top) / rect.height;

      var mouse = new THREE.Vector2(x * 2 - 1, -(y * 2) + 1);
      scope.raycaster.setFromCamera(mouse, scope.object.camera);

      var intersections = scope.raycaster.intersectObjects(objects, true);
      return intersections[0] ? intersections[0] : false;
    }
  }, {
    key: 'translateEvent',
    value: function translateEvent(event) {
      var pointer = {};
      // 获取鼠标屏幕坐标
      if (event.touches) {
        if (event.touches.length === 1) {
          event.preventDefault();
          pointer = event.changedTouches[0];
        } else {
          return;
        }
      } else {
        event.preventDefault();
        pointer = event;
      }

      var rect = this.domElement.getBoundingClientRect();
      if (pointer.clientX < rect.left) return;
      if (pointer.clientX > rect.right) return;
      if (pointer.clientY < rect.top) return;
      if (pointer.clientY > rect.bottom) return;

      return pointer;
    }
  }]);

  return JointControls;
}();

