function ChangeColorEvent() {
  var camera = null;
  var domElement = void 0;
  var objects = null;
  var raycaster = null;
  var color = null;

  function doIntersectObjects(pointer, objects) {
    var rect = domElement.getBoundingClientRect();
    var x = (pointer.clientX - rect.left) / rect.width;
    var y = (pointer.clientY - rect.top) / rect.height;
    var mouse = new THREE.Vector2(x * 2 - 1, -(y * 2) + 1);

    raycaster.setFromCamera(mouse, camera);
    var intersections = raycaster.intersectObjects(objects, true);

    return intersections;
  }

  function onPointerDown(event) {
    if (!color) {
      return;
    }
    var pointer = event.changedTouches ? event.changedTouches[0] : event;
    event.preventDefault();

    var intersects = doIntersectObjects(pointer, objects);
    if (intersects.length > 0) {
      var obj = intersects[0].object;
      if (obj.parent instanceof _Plane2.default) {
        var objPlane = obj.parent;
        objPlane.changeFaceColor(color);
        (0, _projectionAction.showPlaneColor)(objPlane.index, color);
      }
    }
  }

  this.setColor = function (mycolor) {
    color = mycolor;
  };

  this.bindElementEvent = function (iCamera, doElement, objs) {
    camera = iCamera;
    domElement = doElement;
    objects = objs;
    raycaster = new THREE.Raycaster();
    raycaster.linePrecision = 1;
    domElement.addEventListener('mousedown', onPointerDown, false);
    domElement.addEventListener('touchstart', onPointerDown, false);
  };

  this.removeElementEvent = function () {
    if (domElement) {
