function StartRotateEvent() {
  var targetRotation = 0;
  var targetRotationOnMouseDown = 0;
  var mouseX = 0;
  var mouseXOnMouseDown = 0;
  var windowHalfX = _constants.CONSTRAINT.xmax / 2;
  // const windowHalfY = CONSTRAINT.ymax / 2
  var domElement = void 0;
  var enabled = void 0;

  function onDocumentMouseDown(event) {
    if (!domElement) {
      return;
    }
    if (enabled === false) return;

    domElement.addEventListener('mousemove', onDocumentMouseMove, false);
    domElement.addEventListener('mouseup', onDocumentMouseUp, false);
    domElement.addEventListener('mouseout', onDocumentMouseOut, false);

    mouseXOnMouseDown = event.clientX - windowHalfX;
    targetRotationOnMouseDown = targetRotation;
  }

  function onDocumentMouseMove(event) {
    if (enabled === false) return;
    mouseX = event.clientX - windowHalfX;

    targetRotation = targetRotationOnMouseDown + (mouseX - mouseXOnMouseDown) * 0.002;
  }

  function onDocumentMouseUp(event) {
    if (enabled === false) return;
    domElement.removeEventListener('mousemove', onDocumentMouseMove, false);
    domElement.removeEventListener('mouseup', onDocumentMouseUp, false);
    domElement.removeEventListener('mouseout', onDocumentMouseOut, false);
  }

  function onDocumentMouseOut(event) {
    if (enabled === false) return;
    domElement.removeEventListener('mousemove', onDocumentMouseMove, false);
    domElement.removeEventListener('mouseup', onDocumentMouseUp, false);
    domElement.removeEventListener('mouseout', onDocumentMouseOut, false);
  }

  function onDocumentTouchStart(event) {
    if (enabled === false) return;
    if (event.touches.length === 1) {
      mouseXOnMouseDown = event.touches[0].pageX - windowHalfX;
      targetRotationOnMouseDown = targetRotation;
    }
  }

  function onDocumentTouchMove(event) {
    if (enabled === false) return;
    if (event.touches.length === 1) {
      event.preventDefault();

      mouseX = event.touches[0].pageX - windowHalfX;
      targetRotation = targetRotationOnMouseDown + (mouseX - mouseXOnMouseDown) * 0.002;

      (0, _projectionAction.updateStartRotate)(targetRotation);
    }
  }

  this.bindElementEvent = function (doElement) {
    domElement = doElement;
    domElement.addEventListener('mousedown', onDocumentMouseDown, false);
    domElement.addEventListener('touchstart', onDocumentTouchStart, false);
    domElement.addEventListener('touchmove', onDocumentTouchMove, false);
    enabled = true;
  };

  this.removeElementEvent = function () {
    enabled = false;
    if (domElement) {
      domElement.removeEventListener('mousedown', onDocumentMouseDown, false);
      domElement.removeEventListener('touchstart', onDocumentTouchStart, false);
      domElement.removeEventListener('touchmove', onDocumentTouchMove, false);
      domElement.removeEventListener('mousemove', onDocumentMouseMove, false);
      domElement.removeEventListener('mouseup', onDocumentMouseUp, false);
      domElement.removeEventListener('mouseout', onDocumentMouseOut, false);

      domElement = undefined;
    }
  };

  this.getTargetRotation = function () {
    return targetRotation;
  };

