/***/ (function(module, __webpack_exports__, __webpack_require__) {

"use strict";

/**
 * 
 * @param presenter Presenter
 * @constructor
 */
function StudentModeLogic(presenter) {
  this.presenter = presenter;
  //ID
  this.examId = '';
  //
  this.pinyin = '';
  //
  this.title = '';
  //
  this.passOfTime = 0;
  //
  this.timerHandler = -1;
  //
  this.questionFinished = false;

  //DOM
  this.viewModel = {
    practiseType: ko.observable(null), //chinese|(english)
    timer: {
      minutes: ko.observable('00'),
      second: ko.observable('00')
    },
    submitter: {
      text: ko.observable(''),
      enabled: ko.observable(false)
    },
    showCard: ko.observable(false),
    showPractise: ko.observable(false),
    showingAnswer: ko.observable(false),
    hasBeenSubmit: ko.observable(false) //
  };
}

StudentModeLogic.prototype = {
  setPattern: function(eventData) {
    this.viewModel.showCard(eventData.showCard);
  },
  //; presenterrun
  initStudentMode: function() {
    var self = this;
    //
    ko.applyBindings(this.viewModel, this.presenter.$view[0]);
    //
    this.presenter.$view.find('.submitButton')
      .on('click', {
        self: this
      }, this._onSubmitButtonClick);
    this.presenter.$view.find('._js_ShowAnswer')
      .on('click', {
        self: this
      }, this._onShowAnswer);
  },
  //
  releaseStudentMode: function() {
    //
    if (this.timerHandler != -1) {
      window.clearInterval(this.timerHandler);
    }
    ko.cleanNode(this.presenter.$view[0]);
  },
  //
  onPageChange: function() {
    //
    this._resetPractise();
  },
  //ExamID
  setExamId: function(examId) {
    this.examId = examId;
  },
  /**
   * 
   * @param eventData
   */
  startWordCardPractise: function(eventData) {
    var self = this;
    //
    this._resetPractise();

    if (eventData.typeCode == "wordCard") { //
      if (eventData.files === undefined || eventData.files.length !== 1 || eventData.pinyin === undefined) { //
        //
        this.presenter.logger.error(',!');
        return;
      } else {
        //
        $.getJSON(eventData.files[0])
          .done(function(strokeData) {
            if (strokeData === undefined) {
              //
              self.presenter.logger.error(',!');
              return;
            }
            self.viewModel.practiseType("chinese");
            //
            $.extend(strokeData, {
              pinyin: eventData.pinyin
            });
            self.pinyin = eventData.pinyin;
            if (!self.viewModel.practiseType()) {
              return;
            }
            if (self.presenter.practiseWordCardService[self.viewModel.practiseType()].service == null) {
              //
              self.presenter._generateWordCardInstance('PractiseCard', {
                identify: eventData.identify,
                cardData: strokeData
              }, "", self.presenter.cardType["chinese"]);
            } else {
              //
              //
              self.presenter.practiseWordCardService[self.viewModel.practiseType()].service.changeCard({
                identify: eventData.identify,
                cardData: strokeData
              }).done(function() {
                self.afterGenerateCard();
              });
            }
          });
      }
    } else if (eventData.typeCode == "englishCard") {
      if (eventData.files === undefined) { //
        //
        this.presenter.logger.error(',!');
        return;
      } else {
        this.title = (eventData.wordprop == "phrase" || eventData.wordprop == "" ? "" : eventData.wordprop + ".") + eventData.paraphrase; //
        this.viewModel.practiseType("english");
        if (self.presenter.practiseWordCardService[self.viewModel.practiseType()].service == null) {
          //
          self.presenter._generateWordCardInstance('PractiseCard', {
            identify: eventData.identify,
            cardData: {
              answer: eventData.answer,
              files: eventData.files,
              paraphrase: eventData.paraphrase,
              wordprop: eventData.wordprop
            }
          }, "", self.presenter.cardType["english"]);
        } else {
          //
          //
          self.presenter.practiseWordCardService[self.viewModel.practiseType()].service.changeCard({
            identify: eventData.identify,
            cardData: {
              answer: eventData.answer,
              files: eventData.files,
              paraphrase: eventData.paraphrase,
              wordprop: eventData.wordprop
            }
          }).done(function() {
            self.afterGenerateCard();
            self.presenter.practiseWordCardService["english"].service.setWriterStyle();
          });
        }
      }
    }
  },
  afterGenerateCard: function() {
    var self = this;
    //
    if (!self.viewModel.practiseType()) {
      return;
    }
    this.presenter.practiseWordCardService[self.viewModel.practiseType()].identify = this.presenter.practiseWordCardService[self.viewModel.practiseType()].service.getIdentify();
    //
    this.viewModel.showPractise(true);
    if (this.timerHandler == -1) {
      //
      this.timerHandler = window.setInterval(function() {
        self.passOfTime++;
        //
        var second = self.passOfTime % 60;
        var minutes = parseInt(self.passOfTime / 60);
        self.viewModel.timer.minutes(_.padStart(minutes.toString(), 2, '0'));
        self.viewModel.timer.second(_.padStart(second.toString(), 2, '0'));
      }, 1000);
    }
  },
  /**
   * 
   * @param eventData
   */
  sendAnswerCallback: function(eventData) {
    if (eventData.item == 'nosubmit') {
      this.presenter.logger.debug(',');
      return;
    }

    var submitResult = eventData.value.result;
    //
    if (this.questionFinished == true) {
      this.viewModel.submitter.enabled(false);
      this.viewModel.submitter.text('');
      this.viewModel.hasBeenSubmit(true);
    } else {
      if (submitResult == true) {
        //
        //
        this.viewModel.hasBeenSubmit(true);
        this.viewModel.submitter.text('');
      } else {
        //
        this.viewModel.submitter.enabled(true);
        this.viewModel.submitter.text('');
      }
    }
  },
  /**
   * 
   * @param eventData
   */
  submitDetail: function(eventData) {
    var type = eventData.type;
    var self = this;
    if (type === 'get') {
      //
      if (!self.viewModel.practiseType()) {
        return;
      }
      var resultDetail = this.presenter.practiseWordCardService[self.viewModel.practiseType()].service.getResultDetail();
      if (resultDetail == undefined || resultDetail.lenght == 0) {
        this.presenter.logger.debug('');
        return;
      }
      var savedData = [];
      $.each(resultDetail, function(index, item) {
        savedData.push(item.value);
      });
      //
      var sendData = {
        source: 'WordCardController',
        type: 'submit',
        value: {
          examId: this.examId,
          answerFiles: []
        }
      };
      ClassroomUtils.saveFile(savedData)
        .done(function(files) {
          for (var i = 0; i < files.length; i++) {
            //
            sendData.value.answerFiles.push({
              type: 'image',
              url: files[i] != undefined ? files[i] : '',
              extra: JSON.stringify({
                fileKey: resultDetail[i].key,
                pinyin: self.pinyin,
                title: self.title
              })
            });
          }
          //
          self.presenter.$dispatchEvent('SubmitDetail', PresenterEventType.IC_PLAYER_EVENT, sendData);
        })
        .fail(function(errorMsg) {
          self.presenter.logger.debug('' + errorMsg);
        });
    } else if (type === 'result') {
      //
      if (eventData.value.result) {
        self.presenter.logger.info('' + eventData.value.message);
      } else {
        self.presenter.logger.error('' + eventData.value.message);
      }
    }
  },
  //
  sendAnswerStop: function(eventData) {
    this.presenter.logger.debug('');
    //
    this.questionFinished = true;
    //
    this.presenter.practiseWordCardService[this.viewModel.practiseType()].service.lockQuestion();
    //
    this.viewModel.submitter.enabled(false);
    this.viewModel.submitter.text('');
    //
    if (this.timerHandler != -1) {
      window.clearTimeout(this.timerHandler);
    }
    var self = this;
    //
    var sendData = {
      questionId: '',
      answer: '',
      notSubmit: false
    };
    //
    if (eventData.isForceStop || this.viewModel.hasBeenSubmit()) {
      sendData.notSubmit = true;
      delete sendData.questionId;
      delete sendData.answer;
      self.presenter.$dispatchEvent('sendAnswer', PresenterEventType.IC_PLAYER_EVENT, sendData);
    } else {
      //
      this.presenter.practiseWordCardService[self.viewModel.practiseType()].service.getResult()
        .done(function(data) {
          //,
          self._sendAnswerToTeacher(data.answer.user_response[0], sendData);
        })
        .fail(function(errorMsg) {
          self.presenter.logger.error('' + errorMsg);
        });
    }
  },
  /**
   * 
   * @private
   */
  _onShowAnswer: function(event) {
    var self = event.data.self;
    if (self.viewModel.showingAnswer()) {
      return;
    }

    //showAnswer
    self.presenter.practiseWordCardService.english.service.showAnswer();
    //showingAnswer
    self.viewModel.showingAnswer(true);
  },
  /**
   * 
   */
  _onSubmitButtonClick: function(event) {
    var self = event.data.self;
    //,Return
    if (self.viewModel.submitter.enabled() === false) {
      return;
    }
    var sendData = {
      questionId: '',
      answer: '',
      notSubmit: false
    };
    //
    self.viewModel.submitter.enabled(false);
    self.viewModel.submitter.text('...');
    //
    self.presenter.practiseWordCardService[self.viewModel.practiseType()].service.lockQuestion();
    //
    self.presenter.practiseWordCardService[self.viewModel.practiseType()].service.getResult()
      .done(function(data) {
        //
        if (data.answer.user_response == undefined || data.answer.user_response.length == 0) {
          self.viewModel.submitter.enabled(true);
          self.viewModel.submitter.text('');
          //
          self.presenter.practiseWordCardService[self.viewModel.practiseType()].service.unlockQuestion();
        } else {
          //
          if (data.answerState === 'NO_ANSWER') {
            //
            self.viewModel.submitter.enabled(true);
            self.viewModel.submitter.text('');
            //
            self.presenter.practiseWordCardService[self.viewModel.practiseType()].service.unlockQuestion();
          } else {
            self._sendAnswerToTeacher(data.answer.user_response[0], sendData);
          }
        }
      })
      .fail(function(errorMsg) {
        self.presenter.logger.error('' + errorMsg);
      });
  },
  /**
   * 
   * @private
   */
  _resetPractise: function() {
    //
    this.viewModel.submitter.text('');
    this.viewModel.submitter.enabled(false);
    //
    this.viewModel.timer.minutes('00');
    this.viewModel.timer.second('00');
    this.viewModel.showingAnswer(false);
    //
    this.viewModel.hasBeenSubmit(false);
    this.passOfTime = 0;
    this.questionFinished = false;
    //
    this.viewModel.showPractise(false);
    //
    this.examId = this.pinyin = '';
    //
    if (this.viewModel.practiseType() && this.presenter.practiseWordCardService[this.viewModel.practiseType()].service != null) {
      this.presenter.practiseWordCardService[this.viewModel.practiseType()].service.resetQuestion({
        trigger: "ExitQuestion"
      });
    }
    //
    if (this.timerHandler != -1) {
      window.clearInterval(this.timerHandler);
      this.timerHandler = -1;
    }
  },
  questionStateChange: function(eventData) {
    if (eventData.canSubmit !== undefined) {
      if (eventData.canSubmit == true) {
        this.viewModel.submitter.enabled(true);
      } else {
        this.viewModel.submitter.enabled(false);
      }
    }
  },
  //
  _sendAnswerToTeacher: function(thumbUrl, sendData) {
    var self = this;
    if (thumbUrl == undefined || thumbUrl == '') {
      this.presenter.logger.error('!');
      return;
    }
    this._toDataUrl(thumbUrl, function(base64) {
      base64 = base64.replace(/data:(application|image)\/(octet-stream|webp|png);base64,/, ''); //webppngbase64,ios
      sendData.answer = JSON.stringify({
        data: [{
          type: 'thumb',
          value: base64,
          extra: {}
        }]
      });
      self.presenter.logger.debug('');
      self.presenter.$dispatchEvent('sendAnswer', PresenterEventType.IC_PLAYER_EVENT, sendData);
    });
  },
  _toDataUrl: function(url, callback) {
    var xhr = new XMLHttpRequest();
    xhr.responseType = 'blob';
    xhr.onload = function() {
      var reader = new FileReader();
      reader.onloadend = function() {
        callback(reader.result);
      };
      reader.readAsDataURL(xhr.response);
    };
    xhr.open('GET', url);
    xhr.send();
  }
};

