    _sendButtonReset: function(disabled) {
      var self = this;
      var $button = self.$view.find('.send_to_student');
      var disabledStyle = 'com_btn_disabled';
      if ($button.length <= 0) return false;
      if (disabled) {
        !$button.hasClass(disabledStyle) && $button.addClass(disabledStyle);
      } else {
        $button.hasClass(disabledStyle) && $button.removeClass(disabledStyle);
      }
    },
