    run: function(view, model) {
      this.$view = $(view);
      this.model = model;
      var self = this;
      var deferred = $.Deferred();
      //ICPlayer_interface
      this.__interface = {
        execute: self._createDelegate(self._execute, [], true, self),
        isPointInPath: self._createDelegate(self._inPointInPath, [], true, self)
      };
      if (this.isPPTShell || !!this.urlParams.live_terminal) {
        //
        this.timerHandler = -1;
        this.totalSecond = 0;
        $.ajax({
          url: this.basePath + 'resources/html/teacher_mode.html',
          type: 'GET',
          cache: false,
          success: function(data) {
            self.$view.append(data);
            //PPTShellDOM
            self.viewModel = {
              practiseType: ko.observable(null), //chinese|(english)
              practiseModel: ko.observable(false), //
              inPractise: ko.observable(true), //
              maskShow: ko.observable(false), //
              sending: ko.observable(false), //
              popHint: {
                isShow: ko.observable(false), //
                hintText: ko.observable('') //
              },
              maskZIndex: ko.observable(300), //
              inExam: ko.observable(false), //,
              //
              timer: {
                minutes: ko.observable('00'),
                seconds: ko.observable('00')
              },
              //
              progress: {
                now: ko.observable(0),
                total: ko.observable(0)
              }
            };
            //Container
            self.showWrodCardContainer = self.$view.find('.js_show_container');
            self.previewWordCardContainer = self.$view.find('.js_preview_container');
            self.practiseWordCardContainer = self.$view.find('.js_practise_container');
            // self.answerStatisContainer = self.$view.find('.js_answer_statis');       //
            //
            self.practiseComplete = self.$view.find('.js_complete');
            self.practiseReset = self.$view.find('.js_reset');
            self.practiseFinish = self.$view.find('.js_finish');
            self.practiseSendTo = self.$view.find('.js_send_to');
            //
            ko.applyBindings(self.viewModel, self.$view[0]);
            self._layoutReset();
            // 
            var reg = new RegExp("(^|&)" + "hidepush" + "=([^&]*)(&|$)", "i");
            var r = window.location.search.substr(1).match(reg);
            var hidepush = (r != null) ? unescape(r[2]) : null;
            var sendModel = [{
              name: 'buttonText',
              type: 'string',
              value: self._lang['btn_send2student']
            }];
            var footbar = self.$view.find('.js_pratice_footer');
            self.$requireComponent('SendQuestionComponent', sendModel, footbar, null)
              .done(function(presenter) {
                var service = self.sendToStudentService = presenter.getService();
                if (hidepush == "true") {
                  service.presenter.$view.hide();
                }
                service.setSendQuestionInfo({
                  isTool: false
                });
                service.registerSendQuestionEvent('getExerciseData', self._getExerciseData, self);
                service.registerSendQuestionEvent('exerciseStartSuccess', self._exerciseToolStartSuccess, self);
                service.registerSendQuestionEvent('exerciseStartFailure', self._exerciseToolStartFailure, self);
                service.registerSendQuestionEvent('beforeSendQuestion', self._beforeSendQuestion, self);
                deferred.resolve();
              });
            //D //D
            self.$requireComponent('AnswerClassStatis', [{
              name: "questionType",
              type: 'array',
              value: ['WordCard', 'EnglishCard']
            }], null, null);
            self._replaceLang.call(self);
          }
        })

      } else if (this.isApp && this.isStudent) {
        $.ajax({
          url: this.basePath + 'resources/html/student_mode.html',
          type: 'GET',
          cache: false,
          success: function(data) {
            self.$view.append(data);
            //
            self.practiseWordCardContainer = self.$view.find('.js_practise_container');

            //PAD
            self.showWrodCardContainer = self.$view.find('.js_show_container');

            self.studentModeLogic = new __WEBPACK_IMPORTED_MODULE_0__libs_studentModeLogic_js__["a" /* default */](self);
            //
            self.studentModeLogic.initStudentMode();
            deferred.resolve();
            self._layoutReset();
            self._replaceLang.call(self);
          }
        });
      } else {
        deferred.resolve();
      }
      //i18n
      self.$requireComponent('I18nFilter', null, null, null);
      return deferred;
    },
