          $(e.target).removeClass('hover');
        });
        //
        //
        this.practiseComplete.on('click', function() {
          //
          if (self.viewModel.sending() === true) {
            return;
          }
          //
          if (self.viewModel.practiseType() && self.practiseWordCardService[self.viewModel.practiseType()].identify != '' &&
            self.practiseWordCardService[self.viewModel.practiseType()].service != undefined) {
            //
            self.practiseWordCardService[self.viewModel.practiseType()].service.lockQuestion();
            self.viewModel.inPractise(false);
          }
        });
        //
        this.practiseReset.on('click', function() {
          //
          if (self.viewModel.sending() === true) {
            return;
          }
          //
          if (self.viewModel.practiseType() && self.practiseWordCardService[self.viewModel.practiseType()].identify != '' &&
            self.practiseWordCardService[self.viewModel.practiseType()].service != undefined) {
            //
            self.practiseWordCardService[self.viewModel.practiseType()].service.unlockQuestion();
            //
            self.practiseWordCardService[self.viewModel.practiseType()].service.resetQuestion({
              trigger: "ReAnswer"
            });
            self.viewModel.inPractise(true);
          }
        });
        //
        this.practiseFinish.on('click', function() {
          //
          if (self.viewModel.sending() === true) {
            return;
          }
          if (self.viewModel.practiseType() && self.practiseWordCardService[self.viewModel.practiseType()].identify != '' &&
            self.practiseWordCardService[self.viewModel.practiseType()].service != undefined) {
            //
            var currentIdentify = self.practiseWordCardService[self.viewModel.practiseType()].identify;
            //
            self.practiseWordCardService[self.viewModel.practiseType()].service.finishQuestion();
            //
            self.viewModel.maskShow(false);
            self.viewModel.practiseModel(false);
            //
            self.practiseWordCardService[self.viewModel.practiseType()].identify = '';
            self.practiseWordCardService[self.viewModel.practiseType()].phonetic = '';
            self.viewModel.practiseType(null); //self.viewModel.practiseType()  null
          }
        });
        //101PPT
        this.playerController.registerFlow('NotifyForWordCard', {
          scope: 'tool',
          validate: function(data) {
            return true;
          },
