    _getExerciseData: function(data) {
      var practiseType = this.viewModel.practiseType();
      if (!practiseType) {
        return;
      }
      var identify = this.practiseWordCardService[practiseType].identify;
      if (identify == '' || (practiseType == PractiseType.chinese && !this.cardData)) {
        throw Error('null of word card identify or data');
      }
      var ct = this._getCardTypeByIdentify(this.showWordCardService, identify);
      data.questionInfo = {
        id: identify,
        type_code: ct.type_code,
        type_name: ct.type_name,
        dispatchOnly: true
      };
      var eventData = {};
      //
      if (ct.type_code == CardType.chinese.type_code) {
        eventData = {
          identify: identify,
          pinyin: this.practiseWordCardService[practiseType].phonetic,
          files: [],

          typeCode: CardType.chinese.type_code,
          filesType: "json" //
        };
        eventData.files.push(this.cardData);
      } else if (ct.type_code == CardType.english.type_code) {
        eventData = { //
          identify: identify,
          answer: this.cardData.answer,
          pinyin: "",
          paraphrase: this.cardData.paraphrase,
          wordprop: this.cardData.wordprop,
          files: this.cardData.files,
          typeCode: CardType.english.type_code,
          filesType: "image" //
        };
      }
      data.extraData = {
        eventName: "StartWordCardPractise",
        eventData: eventData
      };
    },
