      // if (this.viewModel.practiseType() == PractiseType.english && this.practiseWordCardService[PractiseType.english].service.setWriterStyle) {
      //     this.practiseWordCardService[PractiseType.english].service.setWriterStyle();
      // }
      //
      window.clearInterval(this.timerHandler);
      this.timerHandler = window.setInterval(function() {
        self.totalSecond++;
        //
        var second = self.totalSecond % 60;
        var minutes = parseInt(self.totalSecond / 60);
        self.viewModel.timer.minutes(_.padStart(minutes.toString(), 2, '0'));
        self.viewModel.timer.seconds(_.padStart(second.toString(), 2, '0'));
      }, 1000);
      //
      this.viewModel.sending(false);
      //
      if (this.sendToStudentService) {
        this.sendToStudentService.setVisible(true);
      }
    },
