    _generateWordCardInstance: function(type, data, scene, cardtype) {
      var self = this;
      var containerId = randomWord(false, 8);
      var presenterId = type + '_' + containerId;
      var cardContainer = $('<div id="' + containerId + '"></div>');


      // 
      data.isOnline = self._isOnline();

      if (type == CardScene.SHOW_CARD || type == CardScene.PREVIEW_CARD || type == CardScene.FullPREVIEW_CARD) {
        if (type == CardScene.SHOW_CARD) {
          //
          cardContainer.addClass('_showContainer');
          this.showWrodCardContainer.append(cardContainer);
        } else if (type == CardScene.PREVIEW_CARD || type == CardScene.FullPREVIEW_CARD) {
          //
          cardContainer.addClass('_previewContainer');
          this.previewWordCardContainer.append(cardContainer);
        }

        //
        this.$requireComponent(cardtype.teacher, [{
          name: '__Id',
          type: 'string',
          value: presenterId
        }, {
          name: 'env',
          type: 'string',
          value: type
        }], cardContainer)
          .done(function(presenter) {
            var service = presenter.getService();
            //
            service.changeCard(data)
              .done(function() {
                var identify = service.getIdentify();
                if (scene == 'InClass') {
                  //
                  //
                  self.showWordCardService[cardtype.key].push({
                    identify: identify,
                    typeCode: cardtype.type_code,
                    phonetic: data.phonetic,
                    service: service,
                    container: cardContainer
                  });
                  //
                  $.each(self.showWordCardService[cardtype.key], function(index, item) {
                    if (item.identify != identify) {
                      item.container.removeClass('active');
                    }
                  });
                } else if (scene == 'Preview' || scene == 'FullScreenPreview' || scene == 'FullPreview') {
                  //
                  self.previewWordCardService.identify = identify;
                  self.previewWordCardService.service = service;
                  self.previewWordCardService.container = cardContainer;
                }
                service.setPosition({
                  left: -1000,
                  top: -1000
                });

                //
                cardContainer.addClass('show');
                //
                cardContainer.addClass('active');
                //
                var oriSize = service.getOriSize();
                self._adjustCardPosition(type, oriSize); //
              });
          });
      } else if (type == CardScene.PRACTISE_CARD) {
        var practiseType;
        if (cardtype.type_code == CardType.chinese.type_code) {
          practiseType = PractiseType.chinese;
        } else if (cardtype.type_code == CardType.english.type_code) {
          practiseType = PractiseType.english;
        }

        //
        cardContainer = this.practiseWordCardContainer.find("." + practiseType + ""); //
        cardContainer.attr("id", containerId);
        //
        this.$requireComponent(cardtype.student, [{
          name: '__Id',
          type: 'string',
          value: presenterId
        }], cardContainer)
          .done(function(presenter) {
            var service = presenter.getService();
            self.practiseWordCardService[practiseType].service = service;
            self.practiseWordCardService[practiseType].phonetic = data.cardData.pinyin;
            self.practiseWordCardService[practiseType].cardData = data.cardData;
            self.practiseWordCardService[practiseType].container = cardContainer;
            //undefined;
            if (data != undefined) {
              //
              service.changeCard(data)
                .done(function() {
                  //PPTShellPC
                  if (self.isPPTShell || self.isPC) {
                    //
                    self._goPractiseFrame(practiseType, service.getIdentify(), data);
                  }
                  //
                  else if (self.isApp || self.isStudent) {
                    self.studentModeLogic.afterGenerateCard();
                  }
                  if (service.setWriterStyle) { //canvas
                    service.setWriterStyle();
                  }
                });
            }
          })
      }
    },
    //
    _adjustCardPosition: function(type, oriSize) {
      //
      var cardCount = 0;
      if (type == CardScene.SHOW_CARD) {
        $.each(this.showWordCardService, function(k, v) {
          $.each(v, function(index, item) {
            if (item.identify != '') {
              cardCount++;
            }
          });
        });
      } else if (type == CardScene.PRACTISE_CARD || type == CardScene.PREVIEW_CARD || type == CardScene.FullPREVIEW_CARD) {
        cardCount = 1;
      }

      //
      /*var cardPosition = [];
      var baseLineLeft = document.body.clientWidth / 2 - 200;
      var baseLineTop = document.body.clientHeight / 2 - 200;
      for (var count = 0; count < cardCount; count++) {
      		var left = baseLineLeft + (count * 50);
      		var top = baseLineTop + (count * 50);
      		cardPosition.push({
      				left: left,
      				top: top
      		});
      }*/

      var fontSize = parseFloat(this.$view.css("font-size"));
      var simpleSize = {
        width: (oriSize && oriSize.width) ? oriSize.width : fontSize * (17.41667 + 9.66667),
        height: (oriSize && oriSize.height) ? oriSize.height : fontSize * 29.79167
      };
      var simplePosition = {
        left: (document.body.clientWidth - simpleSize.width) / 2,
        top: (document.body.clientHeight - simpleSize.height) / 2
      };
      var posCount = 0;
      if (type == CardScene.SHOW_CARD) {
        var oriSize,
          position,
          l,
          t;
        $.each(this.showWordCardService, function(k, v) {
          $.each(v, function(index, item) {
            if (item.identify != '') {
              //
              oriSize = item.service.getOriSize();
              l = (document.body.clientWidth - oriSize.width) / 2 + (index * 50);
              t = (document.body.clientHeight - oriSize.height) / 2 + (index * 50);
              position = {
                left: l,
                top: t
              };
              item.service.setPosition(position);
              //
              //item.service.setScale(cardCount);
              posCount++;
            }
          });
        });
      } else if (type == CardScene.PRACTISE_CARD) {
        if (this.practiseWordCardService.chinese.service) {
          this.practiseWordCardService.chinese.service.setPosition(simplePosition);
        }
        if (this.practiseWordCardService.english.service) {
          this.practiseWordCardService.english.service.setPosition(simplePosition);
        }
      } else if (type == CardScene.PREVIEW_CARD || type == CardScene.FullPREVIEW_CARD) {
        this.previewWordCardService.service.setPosition(simplePosition);
      }
    },
    _getCardTypeByIdentify: function(showWordCardService, identify) {
      var key,
        hasFind;
      $.each(showWordCardService, function(k, v) {
        if (hasFind) {
          return false;
        }
        $.each(v, function(index, item) {
          if (item.identify == identify) {
            key = k;
            hasFind = true;
            return false;
          }
        });
      });
      return CardType[key];
    },
